const searchButton = document.querySelector(".search-button"),
    popup = document.querySelector(".modal"),
    form = popup.querySelector("form"),
    arrivalDate = popup.querySelector("[name=arrival-date]"),
    chekOutDate = popup.querySelector("[name=check-out-date]"),
    adults = popup.querySelector("[name=adults]"),
    children = popup.querySelector("[name=children]");
searchButton.addEventListener("click", function(e) { e.preventDefault(), popup.classList.toggle("modal-show"), arrivalDate.focus() }), form.addEventListener("submit", function(e) { arrivalDate.value && chekOutDate.value && adults.value && children.value || e.preventDefault() });
